"""
Create a program to convert the input sentence to Pig Latin.
"""

def pig_latin(sentence):
    words = sentence.split()
    new_words = []
    for word in words:
        if word[0] in 'aeiou':
            new_words.append(word + 'ay')
        else:
            new_words.append(word[1:] + word[0] + 'ay')
    return ' '.join(new_words)

