public static int mostFrequentElement(int[] arr) { 
  int count = 0; 
  int maxCount = 0; 
  int mostFrequent = arr[0]; 

  for (int i = 0; i < arr.length; i++) { 
    count = 0; 
    for (int j = 0; j < arr.length; j++) { 
      if(arr[i] == arr[j]) { 
        count++; 
      } 
      if (count > maxCount) { 
        maxCount = count; 
        mostFrequent = arr[i]; 
      } 
    } 
  } 
  return mostFrequent; 
}

