def are_anagrams(str1, str2):
    if len(str1) != len(str2):
        return False
    else:
        counts = dict()
        for char in str1:
            counts[char] = counts.get(char, 0) + 1
        for char in str2:
            if char not in counts or counts[char] == 0:
                return False
            counts[char] -= 1
    return True

