{
    if (string.ReferenceEquals(s1, s2))
        return true;
    if (s1 == null || s2 == null)
        return false;
    if (s1.Length != s2.Length)
        return false;

    int[] charCount = new int[256];

    foreach (char c in s1){
        charCount[c]++;
    }

    foreach (char c in s2) {
        if (--charCount[c] < 0)
            return false;
    }

