def numTrees(n): 
    if n == 0: 
        return 1
    else: 
        num = 0
        for roots in range(1, n+1): 
            leftNum = numTrees(roots - 1) 
            rightNum = numTrees(n - roots) 
            num += leftNum * rightNum 
