public static double StandardDev(float[] data) 
{ 
    double mean = 0.0, sum_deviation = 0.0; 
    for(double num : data) 
        mean += num; 
    mean = mean / data.length; 
    for(double num: data) 
        sum_deviation += (num - mean)*(num - mean); 
