public static string LCS(string s1, string s2) 
{ 
    int m = s1.Length;
    int n = s2.Length;
    int[,] L = new int[m + 1, n + 1];  

    for (int i = 0; i <= m; i++) 
    { 
        for (int j = 0; j <= n; j++) 
        { 
            if (i == 0 || j == 0) 
                L[i, j] = 0; 
            else if (s1[i - 1] == s2[j - 1]) 
                L[i, j] = L[i - 1, j - 1] + 1; 
            else
                L[i, j] = Math.Max(L[i - 1, j], L[i, j - 1]); 
        } 
    }  

    int index = L[m, n]; 
    int temp = index; 

    char[] lcs = new char[index + 1]; 
    lcs[index] = '\0'; 

    int i = m, j = n; 
    while (i > 0 && j > 0) 
    { 
        if (s1[i - 1] == s2[j - 1]) 
        { 
            lcs[index - 1] = s1[i - 1]; 
            i--;
            j--;
            index--; 
        }  

        else if (L[i - 1, j] > L[i, j - 1]) 
            i--; 
        else
            j--;
    }  
    return new string(lcs); 
}

