class CaesarCipher:

    def __init__(self, shift):
        self.shift = shift

    def encode(self, string):
        cipher = ''
        for char in string: 
            if char == ' ':
                cipher = cipher + char
            elif  char.isupper():
                cipher = cipher + chr((ord(char) + self.shift - 65) % 26 + 65)
            else:
                cipher = cipher + chr((ord(char) + self.shift - 97) % 26 + 97)
        return cipher

