def perfect_numbers(n):
    perfect_numbers = []
    for number in range(1, n+1):
        sum_divisors = 0
        for divisor in range(1, number):
            if number % divisor == 0:
                sum_divisors += divisor
        if sum_divisors == number:
            perfect_numbers.append(number)
    return perfect_numbers

