class Stack:
	def __init__(self):
		self.head = None
		self.num_elements = 0

	def push(self, element):
		new_node = Node(element)
		if self.head == None:
			self.head = new_node
		else:
			new_node.next = self.head
			self.head = new_node
		self.num_elements += 1

	def pop(self):
		if self.is_empty():
			return None
		temp = self.head
		self.head = self.head.next
		self.num_elements -= 1
		return temp.data

	def is_empty(self):
		if self.num_elements == 0:
			return True
		return False

	def top(self):
