"""
Construct an algorithm to find the longest common prefix string amongst an array of strings
"""
def longest_common_prefix(strs):
    prefix = strs[0]
    i = 0
    while i < len(prefix):
        for string in strs:
            if i >= len(string) or string[i] != prefix[i]:
                return prefix[0:i]
        i += 1
    return prefix

