def frequent_words(text):
    """Find the most and least frequent words in a given text"""
    words = text.split(' ')
    word_count = dict()

    for w in words:
        if w not in word_count:
            word_count[w] = 1
        else:
            word_count[w] += 1

    most_frequent_word = max(word_count, key=word_count.get)
