def longest_substring(string):
    longest_substring = ""
    curr_substring = ""
    char_set = set()
    for char in string:
        if char not in char_set:
            curr_substring += char
            char_set.add(char)
        else:
            if len(curr_substring) > len(longest_substring):
                longest_substring = curr_substring
            curr_substring = char
            char_set = set(char)
    if len(curr_substring) > len(longest_substring):
        longest_substring = curr_substring
    return len(longest_substring)

