def max_sum(arr):
    result = [[None] * len(arr[0]) for _ in range(len(arr))]
    result[0][0] = arr[0][0]
    for i in range(1, len(arr[0])):
        result[0][i] = arr[0][i] + result[0][i - 1]
    for i in range(1, len(arr)):
        result[i][0] = arr[i][0] + result[i - 1][0]
    for i in range(1, len(arr)):
        for j in range(1, len(arr[0])):
