        this.x = x;
        this.y = y;
        this.z = z;
    }

    // Dot product
    double dotProduct(Vector3d vec2) {
        double result = this.x * vec2.x + this.y * vec2.y + this.z * vec2.z;
        return result;
    }

    // Normalization
    Vector3d normalize() {
        double magnitude = Math.sqrt(this.x * this.x + this.y * this.y + this.z * this.z);
