    """Given an array with n numbers, find if there exists three elements in the array whose sum is equal to a given number."""
    array.sort()
    for i in range(len(array) - 2):
        left = i + 1
        right = len(array) - 1
        while left < right:
            temp = array[i] + array[left] + array[right]
            if temp == number:
                return True
            elif temp < number:
                left += 1
            else:
                right -= 1
    return False

