// Class to represent a Stack 
class Stack 
{
    // container to store elements 
    vector<int> v;    

public: 
    void push(int data); 
    bool empty(); 
    void pop(); 
    int top(); 
}; 

void Stack::push(int data) 
{ 
    // inserting an element 
    // on the top of the stack 
    v.push_back(data); 
} 

bool Stack::empty() 
{ 
    // checking if the stack is empty 
    return v.size() == 0; 
} 

void Stack::pop() 
{ 
    // removing element 
    // from the top of the stack 
    if (!empty()) 
    { 
        v.pop_back(); 
    } 
} 

int Stack::top() 
{ 
    // returns the top element 
    // of the stack 
    return v.back(); 
}

# Function to check for prime number 
def isPrime(n): 

    # Corner cases 
    if n <= 1: 
        return False

    # Check from 2 to n-1 
    for i in range(2, n): 
        if n % i == 0: 
            return False

    return True

