def sublist_sum(nums, target):
    result = []
    current_sum = 0
    start = 0
    for end in range(len(nums)):
        current_sum += nums[end]
        while current_sum > target and start < end:
            current_sum -= nums[start]
            start += 1
        if current_sum == target:
            result.append(nums[start:end+1])
    return result

