def findMaxDifference(arr):
    min_element = arr[0]
    max_difference = arr[1] - arr[0]
    for num in arr:
        if num - min_element > max_difference:
            max_difference = num - min_element
        if num < min_element:
            min_element = num
    return max_difference

public static List<List<Integer>> findSumPairs(int arr[], int sum) {
    List<List<Integer>> result = new ArrayList<>();
    Map<Integer, Integer> map = new HashMap<>();

    for (int i = 0; i < arr.length; i++) {
        int target = sum - arr[i];
        if (map.containsKey(target)) {
            result.add(Arrays.asList(map.get(target), i));
        }
        map.put(arr[i], i);
    }
    return result;
}

static void printDiagonals(int arr[][]) {
    int n = arr.length;
    // Print anti-diagonals
    for (int k = 0; k < n; k++) {
        for (int i = 0, j = k; j >= 0; j--, i++) {
            System.out.print(arr[i][j] + " ");
        }
        System.out.println();
    }

    // Print main diagonals
    for (int k = 1; k < n; k++) {
        for (int i = k, j = n - 1; i < n; i++, j--) {
            System.out.print(arr[i][j] + " ");
        }
        System.out.println();
    }
}

