public static int lcm(int a, int b) {
    int gcd = 1;
    for(int i = 1; i <= a && i <= b; ++i)
    {
        // Checks if i is factor of both integers
        if(a % i == 0 && b % i == 0)
            gcd = i;
    }

    int lcm = (a*b) / gcd;
    return lcm;
}

// Algorithm to reverse a singly linked list in O(n) time
//
//
// INPUT:  A singly Linked List
//
// OUTPUT: A reversed singly Linked List

// Initialize three pointers

Node* prev = NULL; 
Node* curr = head;
Node* next = NULL;

// Iterate through the linked list, reversing the links as we go

while (curr != NULL) {
    // Store the next node
    next = curr->next;

    // Reverse the current node's pointer
    curr->next = prev;

    // Advance the pointers one position
    prev = curr;
    curr = next;
}

// Return the reversed linked list
return prev;

def long_increasing_subsequence(arr):
    # Number of items in given array
    n = len(arr)

    # Initialize 'lengths' values for all indices
    lengths = [1]*n

    # Find the longest increasing subsequence
    for i in range(1, n):
        for j in range(i):
            if arr[j] < arr[i] and lengths[j] + 1 > lengths[i]:
                lengths[i] = lengths[j] + 1

    return lengths

// Function to remove comments from a given input string
string removeComments(string input_string)
{
    string output_string = "";
    int flag = 0;

    for (int i = 0; i<input_string.length(); i++)
    {
        // Check for single line comments
        if (input_string[i] == '/' && input_string[i+1] == '/')
            flag = 1;

        // Check for multi-line comments
        if (input_string[i] == '/' && input_string[i+1] == '*')
            flag = 2;

        // If single line or multi-line comment section,
        // ignore the part of the string
        if (flag == 1 || flag == 2)
        {
            if (input_string[i] == '\n')
                flag = 0;
            continue;
        }

        output_string += input_string[i];
    }

    return output_string;
}

