"""
Use a Linked List structure to implement a stack data structure.
"""

class Node:
    def __init__(self, val):
        self.value = val
        self.next = None

class Stack:
    def __init__(self):
        self.top = None
        self.size = 0

    def push(self, val):
        new_node = Node(val)
        new_node.next = self.top
        self.top = new_node
        self.size += 1

    def pop(self):
        if self.top == None:
            return None
        else:
            pop_node = self.top
            self.top = self.top.next
            self.size -= 1
            return pop_node.value

