def binarySearch(array, x): 
    l, r = 0, len(array)-1 
    while l <= r: 

        mid = l + (r - l)//2; 

        # Check if x is present at mid 
        if array[mid] == x: 
            return mid 

        # If x is greater, ignore left half 
        elif array[mid] < x: 
            l = mid + 1

        # If x is smaller, ignore right half 
        else: 
            r = mid - 1

    # If we reach here, then the element 
    # was not present 
    return -1

