    if n == 0:
        return None

    primeCount = 0
    checkNum = 2
    while(primeCount < n):
        isPrime = True
        for i in range(2, checkNum):
            if checkNum % i == 0:
                isPrime = False

        if isPrime == True:
            primeCount += 1

        if primeCount == n:
            return checkNum
        else:
            checkNum += 1

