def find_edit_distance(str1, str2):
    len_str1 = len(str1)
    len_str2 = len(str2)

    matrix = [[0] * (len_str2+1) for _ in range(len_str1+1)]
    for i in range(len_str1+1):
        matrix[i][0] = i
    for j in range(len_str2+1):
        matrix[0][j] = j

    for i in range(1, len_str1+1):
        for j in range(1, len_str2+1):
            if str1[i-1] == str2[j-1]:
                matrix[i][j] = matrix[i-1][j-1]
            else:
                matrix[i][j] = min(matrix[i-1][j]+1, matrix[i][j-1]+1, matrix[i-1][j-1]+1)

    return matrix[len_str1][len_str2]

def longest_increasing_sequence(arr):
    lengths = [1]*len(arr)

    for i in range(1, len(arr)):
        for j in range(i):
            if arr[i] > arr[j]:
                lengths[i] = max(lengths[i], lengths[j] + 1)

    max_length_index = 0
    for i in range(len(lengths)):
        if lengths[i] > lengths[max_length_index]:
            max_length_index = i

    current_index = max_length_index
    sequence = [arr[max_length_index]]
    for i in range(max_length_index-1, -1, -1):
        if lengths[i] == lengths[current_index] - 1 and arr[i] < arr[current_index]:
            sequence.append(arr[i])
            current_index = i
    sequence.reverse()
    return sequence

def oneAway(str1, str2): 
    len1 = len(str1) 
    len2 = len(str2) 
    if abs(len1 - len2) > 1: 
        return False
    i = 0
    j = 0 
    count = 0 
    while i < len1 and j < len2: 
        if str1[i] != str2[j]: 
            if count == 1: 
                return False
            count = 1
            if len1 > len2: 
                i += 1
            elif len1 < len2: 
                j += 1 
            else: 
                i +=1
                j += 1 
        else: 
            i += 1
            j += 1
    if i < len1 or j < len2: 
        count += 1 
    return count == 1

