def rod_cutting(arr, n):
    dp = [-1 for _ in range(n+1)]
    dp[0] = 0
    for i in range(1, n+1):
        for j in range(i):
            dp[i] = max(dp[i], arr[j] + dp[i - j - 1])
    return dp[n]

