    if root is None:
        return None
    queue = []
    queue.append(root)
    while len(queue) > 0:
        res = queue[0]
        queue.pop()
        if res.left is not None:
            queue.append(res.left)
        if res.right is not None:
            queue.append(res.right)
    return res

