def longest_substring(string):
    curr_len = 1
    max_len = 1
    prev_index = 0
    n = len(string)
    visited = [-1] * 256
    visited[ord(string[0])] = 0
    for i in range(1,n):
        prev_index = visited[ord(string[i])]
        if prev_index == -1 or (i - curr_len > prev_index):
            curr_len+=1
        else:
            if curr_len > max_len:
                max_len = curr_len
            curr_len = i - prev_index
        visited[ord(string[i])] = i
    if curr_len > max_len:
        max_len = curr_len
    return max_len

