def merge_lists(list1, list2):
    final_list = []
    i, j = 0, 0

    while i < len(list1) and j < len(list2):
        if list1[i] < list2[j]:
            final_list.append(list1[i])
            i += 1
        else:
            final_list.append(list2[j])
            j += 1

    while i < len(list1):
        final_list.append(list1[i])
        i += 1

    while j < len(list2):
        final_list.append(list2[j])
        j += 1

    return final_list

