def min_partition_array(arr):
    total = 0
    for i in arr:
        total += i
    sum1, sum2 = 0, total
    n = len(arr)
    flag = [[False for i in range(total + 1)] for j in range(n + 1)]
    flag[0][0] = True
    for i in range(1, n + 1):
        for j in range(total + 1):
            if arr[i - 1] <= j:
                flag[i][j] = flag[i - 1][j] or flag[i - 1][j - arr[i - 1]]
            else:
                flag[i][j] = flag[i - 1][j]
    for j in range(0, int(total / 2) + 1):
        if flag[n][j]:
            sum1 = j
            sum2 = total - j
            break
    return sum1, sum2

