Node* reverse(Node* head) 
{ 
    Node* current = head; 
    Node* prev = NULL; 
    Node* next = NULL; 
    if (current == NULL) 
        return NULL; 
    while (current != NULL) { 
        next = current->next; 
        current->next = prev; 
        prev = current; 
        current = next; 
    } 
    head = prev; 
    return head; 
}

