def spiral_print(a):
    m = len(a)
    n = len(a[0])

    t = 0   #top
    b = m-1 #bottom
    l = 0   #left
    r = n-1 #right

    dir = 0
    while l<=r and t<=b:

        if dir == 0:
            for i in range(l, r+1):
                print(a[t][i], end=" ")
            t += 1
        elif dir == 1:
            for i in range(t, b+1):
                print(a[i][r], end=" ")
            r -= 1
        elif dir == 2:
            for i in range(r, l-1, -1):
                print(a[b][i], end=" ")
            b -= 1
        elif dir == 3:
            for i in range(b, t-1, -1):
                print(a[i][l], end=" ")
            l += 1

        dir=(dir+1)%4

