def is_prime(n):
    """Returns True if n is a prime number and False otherwise."""
    if n < 2:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    for i in range(3, int(n**0.5) + 1, 2):
        if n % i == 0:
            return False
    return True

def prime_numbers_less_than(n):
    """Returns a list of all prime numbers less than n."""
    primes = []
    for i in range(2, n):
        if is_prime(i):
            primes.append(i)
    return primes

