int calculateAverage(struct node* head) 
{ 
    if (head == NULL) 
        return 0;

    int count = 0;
    int sum = 0;

    struct node* temp = head; 
    while (temp != NULL){ 
        sum += temp->data; 
        temp = temp->next;
        count++; 
    } 
    return sum/count; 
}

