def binary_search(list, goal): 
    start = 0 
    end = len(list) - 1
    while start <= end: 
        mid = (start + end) // 2
        if list[mid] == goal: 
            return mid 
        elif goal < list[mid]: 
            end = mid - 1 
        else: 
            start = mid + 1
    return -1

