public static int binarySearch(int[] array, int x) 
   { 
       int low = 0, high = array.length - 1; 

       while (low <= high)  
       { 
           int mid = (low + high) / 2; 
           if (array[mid] < x) 
               low = mid + 1; 
           else if (array[mid] > x)  
               high = mid - 1; 
           else
               return mid; 
       } 
       return -1; 
   }

