public static int longestIncreasingSubsequence(int[] arr) {
  int[] dp = new int[arr.length];
  int maxLength = 1;

  dp[0] = 1;

  for (int i=1; i<arr.length; i++) {
      int maxVal = 0;
      for (int j=0; j<i; j++) {
          if (arr[j] < arr[i] && dp[j] > maxVal) {
              maxVal = dp[j];
          }
      }
      dp[i] = maxVal + 1;
      if (dp[i] > maxLength) {
          maxLength = dp[i];
      }
  }

  return maxLength;
}

