// reversing the linked list
Node* reverse(Node* head) 
{ 
    Node* prev = NULL; 
    Node* current = head; 
    Node* next; 
    while (current != NULL) { 
        // Store next 
        next = current->next; 

        // Reverse current node's pointer 
        current->next = prev; 

        // Move pointers one position ahead. 
        prev = current; 
        current = next; 
    } 
    head = prev; 
    return head; 
}

