def longest_common_prefix(strs): 

    if not strs: 
        return "" 

    shortest_str = min(strs,key=len) 

    for i, ch in enumerate(shortest_str): 
        for other in strs: 
            if other[i] != ch: 
                return shortest_str[:i]  
    return shortest_str 

