def is_balanced(s): 
    stack = [] 
    for letter in s: 
        if letter in ["(", "[", "{"]: 
            stack.append(letter) 
        else: 
            if len(stack) == 0: 
                return False
            top = stack.pop(-1) 
            if top == '(': 
                if letter == ')':             
                    continue
                else: 
                    return False
            elif top == '[': 
                if letter == ']': 
                    continue
                else: 
                    return False
            elif top == '{': 
                if letter == '}': 
                    continue
                else: 
                    return False
    if len(stack) == 0: 
        return True 
    return False 

