        result_vector = []
        for i in range(len(self.vector)):
            result_vector.append(self.vector[i] + other_vector.vector[i])
        return Vector(result_vector, self.scaler)

    def sub_vector(self, other_vector):
        result_vector = []
        for i in range(len(self.vector)):
