    # Split sentence into words
    words = sentence.split(" ")
    # Keep track of our highest vowel count and the associated word
    highest_count = 0
    most_vowels_word = ""
    # Count the number of vowels in each word
    for word in words:
        num_vowels = 0
        for c in word:
            if c.lower() in ["a", "e", "i", "o", "u"]:
                num_vowels += 1
