    s = set()
    current1 = head1
    current2 = head2
    while current1 is not None:
        s.add(current1.data)
        current1 = current1.next
    result = []
    while current2 is not None:
        if current2.data in s:
            result.append(current2.data)
        current2  = current2.next
    return result

