    eleFreq = {} 
    sortedList = [] 

    # Create a dictionary with frequency of element as key and element as value
    for i in arr: 
        if i in eleFreq: 
            eleFreq[i] += 1
        else: 
            eleFreq[i] = 1

    # Sort the dictionary 
    sortedFreq = sorted(eleFreq.items(), key = lambda eleFreq: eleFreq[1], reverse = True) 

