def greatest_common_divisor(int1, int2):
  if int1 == 0:
    return int2
  elif int2 == 0:
    return int1
  elif int1 == int2:
    return int1
  else:
    if int1 > int2:
      return greatest_common_divisor(int2, int1 % int2)
    else:
      return greatest_common_divisor(int1, int2 % int1)

