// Algorithm to find the longest palindromic substring

int longestPalindromicSubstr(char* str) 
{
	// Variables to store the start and the end of the palindromic substring
	int maxLen = 1;
	int start = 0;
	int len = strlen(str);
	// Table to store boolean value at the index
	// dp[i][j] stores whether the substring str[i..j] is palindrome or not
	int dp[len][len];

	// Initialize all entries to False
	memset(dp, 0, sizeof(dp));

	// All characters of length 1 are palindrome
	for (int i = 0; i < len; i++) 
		dp[i][i] = true;

	// Check for substring of length 2
	for (int i = 0; i < len-1; i++) 
	{
		if (str[i] == str[i+1]) 
		{
			dp[i][i+1] = true;
			start = i;
			maxLen = 2;
		}
	}

	// Check for lengths greater than 2 
	for (int k = 3; k <= len; k++) 
	{ 
		// looping until len-k+1, so that you can get ith character
		for (int i = 0; i < len-k+1; i++) 
		{ 
			int j = i + k - 1; 
			if (dp[i+1][j-1] && str[i] == str[j]) 
			{ 
				dp[i][j] = true; 

				if (k > maxLen) 
				{ 
					start = i; 
					maxLen = k; 
				} 
			} 
		} 
	} 

	return maxLen; 
}

// Algorithm to find the least common ancestor in a binary tree

Node *LCA(Node *root, Node *p, Node *q) 
{ 
 /* Base case */
 if (root == NULL) return NULL;

 // If either p or q matches with root's key, report 
 // the presence by returning root (Note that if a key is 
 // ancestor of other, then the ancestor key becomes LCA 
 if (root == p || root == q) 
    return root; 

 // Look for keys in left and right subtree 
 Node *left = LCA(root->left, p, q); 
 Node *right = LCA(root->right, p, q); 

 // If both of the above calls return Non-NULL, then one key 
 // is present in once sub-tree and other is present in other, 
 // So this node is the LCA 
 if (left != NULL && right != NULL) 
    return root; 

// Otherwise check if left subtree or right subtree is LCA 
return (left != NULL) ? left : right; 
}

