//recursive approach
Node *deleteKey(Node *head, int k) 
{ 
    Node *curr = head; 
    if (curr == NULL) 
        return NULL; 

    // check for head node 
    if (curr->key == k) { 
        Node *temp = curr; 
        head = curr->next; 
        free(temp); 
        return deleteKey(head, k); 
    } 

    // check for other nodes 
    while (curr->next != NULL) { 
        if (curr->next->key == k) { 
            Node *temp = curr->next; 
            curr->next = curr->next->next; 
            free(temp); 
        } 
        curr = curr->next; 
    } 
    return head; 
}

