def max_sum_path(matrix):
    rows, columns = len(matrix), len(matrix[0])
    dp = [[float("-inf") for i in range(columns)] for j in range(rows)]
    dp[0][0] = matrix[0][0]
    for i in range(rows):
        for j in range(columns):
            if i > 0:
                dp[i][j] = max(dp[i][j], dp[i-1][j] + matrix[i][j])
            if j > 0:
                dp[i][j] = max(dp[i][j], dp[i][j-1] + matrix[i][j])
    return dp[-1][-1]

def max_sum_submatrix(matrix):
    rows, columns = len(matrix), len(matrix[0])
    dp = [[0] * columns for _ in range(rows)]
    for i in range(rows):
        for j in range(columns):
            dp[i][j] = matrix[i][j]
            if i > 0:
                dp[i][j] += dp[i-1][j]
            if j > 0:
                dp[i][j] += dp[i][j-1]
            if i > 0 and j > 0:
                dp[i][j] -= dp[i-1][j-1]

    max_sum = float('-inf')
    for r1 in range(rows):
        for r2 in range(r1, rows):
            for c1 in range(columns):
                for c2 in range(c1, columns):
                    current_sum = dp[r2][c2]
                    if r1 > 0:
                        current_sum -= dp[r1-1][c2]

                    if c1 > 0:
                        current_sum -= dp[r2][c1-1]

                    if r1 > 0 and c1 > 0:
                        current_sum += dp[r1-1][c1-1]

                    max_sum = max(max_sum, current_sum)
    return max_sum

